import axios from 'axios';
import './App.css';
import React from 'react';
//import { Routes, Route }  from 'react-router-dom';

function App() {

  const [meters, setMeters]=React.useState([]);
  const [data, setData]= React.useState([]);
  React.useEffect ( ()=> {
    axios.get('/meters')
    .then ((response)=> {
      setMeters(response.data);
    })
    .catch( (err)=> {
      alert(err);
    })
  }, [])

 var getData = function ( event, id) {
   axios.get(`/meters/Meter_ID/?id=${id}`)
   .then ( (response)=> {
     setData(response.data);
   })
   .catch( (err) => {
     alert(err);
   })
 }



  return (
    <div>
    <div className="App">
         meters
    </div>
    <div>
      {meters.length > 0?meters.map((meter, index)=>( <div to="/Details" id={index} onClick={(event)=>{getData(event, meter.id)}}>{meter.label}</div>)):null}
    </div>
    <div>
    {data.length > 0?data.map((infor, index)=>( <div id={index}>{infor.timestampM} {infor.valueM}</div>)):null}
    </div>
    </div>
  );
}

export default App;
